import React, {Component} from 'react';
import TotalSummary from './TotalSummary';
import {connect} from 'react-redux';
import {income, spending} from './store/action/action';

class ExpenseList extends Component {
  render () {
      if (this.props.ttlincome.type == 'INCOME') {
    return (
        <div>
          <span><i></i>{this.props.ttlincome.in}</span>
          <span>{this.props.ttlincome.desc}</span>
          <span>icon</span>
        <hr />
        </div>      
       ) 
      } else {
        return (
        <div></div>  
        )      
      }
  }
}

const mapStateToProps = (state) => {
  return {
    balance: state.balance,
    ttlincome: state.ttlincome,
    ttlspending: state.ttlspending
  }
}

const mapDispatchToProps = () => {
  return {
    income,
    spending
  }
}

export default connect(mapStateToProps,mapDispatchToProps())(ExpenseList);
