import React, {Component} from 'react';
import TotalSummary from './TotalSummary';
import {income, spending} from './store/action/action';

class FormHandle extends Component {
  constructor (props) {
      super(props);
      this.cost = React.createRef();
      this.desc = React.createRef();
      this.expensive = React.createRef();
      this.state = {
          formValue: {
            cost: '',
            desc: ''
        }
      }
  }	
  OnClickAction = () => {
  	this.props.income();
  }
  render () {
  	console.log(this.props.show);
  	let checkDisState = this.props.show;
      if (checkDisState === 'true') {
        return (
            <form onSubmit={this.formSubmit} ref={form => this.expensive = form} id="expensive">
                <div>
                  <label>Add Rs:</label>	
                  <input type="number" value={this.state.formValue.cost} ref={this.const} name="cost"/>
                </div>
                <div>
                  <label>Describtion:</label>
                  <textarea value={this.state.formValue.desc} ref={this.desc} name="desc"/>
                </div>
                <button type="submit" name="submit" onClick={this.OnClickAction}>Submit</button>
            </form>                       
        );
      } else {
          return (
              <div></div>
          );
      } 
  }
}

export default FormHandle;
