import React, {Component} from 'react';

class FormHandle extends Component {
  render () {
      if (checkState === true) {
        return (
            <form onSubmit={this.formSubmit} ref={form => this.eForm = form} id="eForm">
                <div>
                  <label>Add Rs:</label>	
                  <input type="number" value={this.state.formValue.cost} ref={this.const} onChange={this.inputOnChange.bind(this.name)} name="cost"/>
                </div>
                <div>
                  <label>Describtion:</label>
                  <textarea value={this.state.formValue.firstName} ref={this.firstName} onChange={this.inputOnChange.bind(this.name)} name="firstname"/>
                  <input type="text" value={this.state.formValue.firstName} ref={this.firstName} onChange={this.inputOnChange.bind(this.name)} name="firstname" />
                </div>
                <div>
                  <input type="text" value={this.state.formValue.region} ref={this.region} onChange={this.inputOnChange.bind(this.name)} name="region" />
                </div>
                <div>
                  <input disabled type="text" value={this.props.getEcode} ref={this.employeeCode} name="empcode" />
                </div>
                <button type="reset" name="submit" onClick={this.OnClickAction}>Submit</button>
            </form>                       
        );
      } else {
          return (
              <div></div>
          );
      } 
  }
}

export default FormHandle;
