import React, {Component} from 'react';
import FormHandle from './FormHandle'
import ExpenseList from './ExpenseList';
import {connect} from 'react-redux';
import {income, spending} from './store/action/action';

class TotalSummary extends Component {
  constructor (props) {
    super(props);
    this.state = {
      displaySte:  false
    }
  }
  addIncome = () => {
    this.setState({
      displaySte: true
    });
  }
  addSpent = () => {
    this.setState({
      displaySte: true
    });
  }
  render () {
    let formShow = this.state.displaySte; 
    return (
      <div>
        <header>
          <p>Balance</p>
          <h1>{this.props.balance}</h1>
          <span className="income">Income: 0 </span>
          <span className="spent">Spending: 0</span>
        </header>
        <ExpenseList />
        // <button onClick={() => this.props.income()}>Add Income</button>
        <button onClick={this.addIncome()}>Add Income</button>
        <button onClick={this.addSpent()}>Add Spending</button>
        // <button onClick={() => this.props.spending()}>Add Spending</button>
        <FormHandle formShow={formShow} />
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    balance: state.balance
  }
}

const mapDispatchToProps = () => {
  return {
    income,
    spending
  }
}

export default connect(mapStateToProps,mapDispatchToProps())(TotalSummary);
