import React, {Component} from 'react';
import FormHandle from './FormHandle'
import ExpenseList from './ExpenseList';
import {connect} from 'react-redux';
import {income, spending} from './store/action/action';

class TotalSummary extends Component {
  constructor (props) {
    super(props);
    this.state = {
      displaySte:  'false',
      income: 'false',
      spent: 'false'
    }
  }
  addIncome = () => {
    this.setState({
      displaySte: 'true',
      income: 'true',
      spent: 'false'
    });
  }
  addSpent = () => {
    this.setState({
      displaySte: 'true',
      income: 'false',
      spent: 'true'
    });
  }
  render () {
    return (
      <React.Fragment>
        <header>
          <p>Balance</p>
          <h1>{this.props.balance}</h1>
          <span className="income">Income: {this.props.ttlincome} </span>
          <span className="spent">Spending: {this.props.ttlspending}</span>
        </header>
        <ExpenseList />
        <button onClick={this.addIncome}>Add Income</button>
        <button onClick={this.addSpent}>Add Spending</button>
        <FormHandle show={this.state.displaySte} income={this.state.income} spent={this.state.spent} />
        
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    balance: state.balance,
    ttlincome: state.ttlincome,
    ttlspending: state.ttlspending
  }
}

const mapDispatchToProps = () => {
  return {
    income,
    spending
  }
}

export default connect(mapStateToProps,mapDispatchToProps())(TotalSummary);
