import React, {Component} from 'react';

class FormHandle extends Component {
  render () {
      if (checkState === true) {
        return (
            <form onSubmit={this.formSubmit} ref={form => this.eForm = form} id="eForm">
                <div>
                  <label>Add Rs:</label>	
                  <input type="number" value={this.state.formValue.cost} ref={this.const} onChange={this.inputOnChange.bind(this.name)} name="cost"/>
                </div>
                <div>
                  <label>Describtion:</label>
                  <textarea value={this.state.formValue.desc} ref={this.desc} onChange={this.inputOnChange.bind(this.name)} name="desc"/>
                </div>
                <button type="submit" name="submit" onClick={this.OnClickAction}>Submit</button>
            </form>                       
        );
      } else {
          return (
              <div></div>
          );
      } 
  }
}

export default FormHandle;
