import {INCOME, SPENDING} from '../../store/action/action';

// const state = 0;
// const totalIncome = 0;
// const totalSpending = 0;
// const balance = totalIncome - totalSpending;
// const balance = 0;

const reducer = (balance=0, action) => {
	switch (action.type) {
		case INCOME:
			// return balance = balance+totalIncome;
			return balance = parseInt(action.amount) + parseInt(balance);
			break;
		case SPENDING:
			// return balance = balance-totalSpending;
			return balance = action.amount - balance;
			break;
		default:
			return balance;	
	}
}

const ttlincome = (income=0, action) => {
	switch (action.type) {
		case INCOME:
			return income += income;
			break;
		default:
			return income	 
	}
}

export default reducer;
