import {INCOME, SPENDING} from '../../store/action/action';

// const state = 0;
// const totalIncome = 0;
// const totalSpending = 0;
// const balance = totalIncome - totalSpending;
// const balance = 0;

export const balance = (balance=0, action) => {
	switch (action.type) {
		case INCOME:
			// return balance = balance+totalIncome;
			return balance = parseInt(action.amount) + parseInt(balance);
			break;
		case SPENDING:
			// return balance = balance-totalSpending;
			return balance = action.amount - balance;
			break;
		default:
			return balance;	
	}
}

export const ttlincome = (income=0, action) => {
	switch (action.type) {
		case INCOME:
			console.log(income);
			return income += income;
			break;
		default:
			return income;	 
	}
}

export const ttlspending = (spending=0, action) => {
	switch (action.type) {
		case SPENDING:
			return spending += spending;
			break;
		default:
			return spending;	
	}
}
