import React, {Component} from 'react';
import ExpenseList from './ExpenseList'

class TotalSummary extends Component {
  render () {
    return (
      <div>
        <header>
          <p>Balance</p>
          <h1>{this.props.balance}</h1>
          <span className="income">Income: {this.props.income}</span>
          <span className="spent">Spending: {this.props.spent}</span>
        </header>
        <ExpenseList />
        <button onClick="{this.addIncome}">Add Income</button>
        <button onClick="{this.addSpending}">Add Spending</button>
      </div>
    )
  }
}



export default TotalSummary;
