import React, {Component} from 'react';
import FormHandle from './FormHandle'
import ExpenseList from './ExpenseList';
import {connect} from 'react-redux';
import {income, spending} from './store/action/action';

class TotalSummary extends Component {
  constructor (props) {
    super(props);
    this.state = {
      displaySte:  'false',
      in: 'false',
      out: 'false'
    }
  }
  addIncome = () => {
    this.setState({
      displaySte: 'true',
      in: 'true',
      out: 'false'
    });
  }
  addSpent = () => {
    this.setState({
      displaySte: 'true',
      in: 'false',
      out: 'true'
    });
  }
  render () {
    return (
      <React.Fragment>
        <header>
          <p>Balance</p>
          <h1>{this.props.balance}</h1>
          <span className="income">Income: {this.props.ttlincome.income} </span>
          <span className="spent">Spending: {this.props.ttlspending.spending}</span>
        </header>
          <span><i></i>{this.props.ttlincome.in}</span>
          <span>{this.props.ttlincome.desc}</span>
          <span>icon</span>
        <hr />
        <button onClick={this.addIncome}>Add Income</button>
        <button onClick={this.addSpent}>Add Spending</button>
        <FormHandle show={this.state.displaySte} showincome={this.state.in} showspent={this.state.out} />
        
      </React.Fragment>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    balance: state.balance,
    ttlincome: state.ttlincome,
    ttlspending: state.ttlspending
  }
}

const mapDispatchToProps = () => {
  return {
    income,
    spending
  }
}

export default connect(mapStateToProps,mapDispatchToProps())(TotalSummary);
