import React, {Component} from 'react';
import TotalSummary from './TotalSummary';

class FormHandle extends Component {
  constructor (props) {
      super(props);
      this.cost = React.createRef();
      this.desc = React.createRef();
      this.expensive = React.createRef();
      this.state = {
          checkDisState: props.formShow,
          formValue: {
            cost: '',
            desc: ''
        }
      }
  }	
  render () {
  	console.log(this.state.checkDisState);
  	let checkDisState = this.state.checkDisState;
      if (checkDisState === true) {
        return (
            <form onSubmit={this.formSubmit} ref={form => this.expensive = form} id="expensive">
                <div>
                  <label>Add Rs:</label>	
                  <input type="number" value={this.state.formValue.cost} ref={this.const} onChange={this.inputOnChange.bind(this.name)} name="cost"/>
                </div>
                <div>
                  <label>Describtion:</label>
                  <textarea value={this.state.formValue.desc} ref={this.desc} onChange={this.inputOnChange.bind(this.name)} name="desc"/>
                </div>
                <button type="submit" name="submit" onClick={this.OnClickAction}>Submit</button>
            </form>                       
        );
      } else {
          return (
              <div></div>
          );
      } 
  }
}

export default FormHandle;
